// Compute similar columns perfectly, with brute force.
val exact = mat.columnSimilarities()
val exactEntries = exact.entries.map { case MatrixEntry(i, j, u) => ((i, j), u) }

println(s"""\ndoc#\tdoc#\tsimilarity""")
exact.entries.collect().foreach(entry => println(s"""${entry.i}\t${entry.j}\t${entry.value}"""))

val meanAbsoluteError = exactEntries.leftOuterJoin(approxEntries).values.map {
  case (u, Some(v)) =>
    math.abs(u - v)
  case (u, None) =>
    math.abs(u)
}.mean()
