import com.advancedspark.ml.Similarity

val minExactJaccardSimilarityThreshold = 0.01

// Calculate Jaccard Similarity between all distinct item pairs
// Only keep pairs with a Jaccard Similarity above a specific threshold
val similarItemsAboveThresholdRDD = allItemPairsRDD.flatMap(itemPair => {

  val jaccardSim = Similarity.getJaccardSimilarity(itemPair._1, itemPair._2)

  if (jaccardSim >= minExactJaccardSimilarityThreshold)
    Some(itemPair._1.id.toLong, itemPair._2.id.toLong, jaccardSim.toDouble)
  else
    None
})

val similarItemPairCount = similarItemsAboveThresholdRDD.count()

similarItemsAboveThresholdRDD.collect().mkString(",")

import org.apache.spark.graphx._

val similarItemsAboveThresholdEdgeRDD = similarItemsAboveThresholdRDD.map(rdd => {
  Edge(rdd._1.toLong, rdd._2.toLong, rdd._3.toDouble) 
})

val similarityGraph = Graph.fromEdges(similarItemsAboveThresholdEdgeRDD, 0L)
  .cache()

import com.advancedspark.ml.graph.Dijkstra

// Toy Story (Me)
val src = 1 

// Sudden Death (You)
val dest = 9 

// Generate Shortest Path for all nodes in the graph
val shortestPathGraph = Dijkstra.run(similarityGraph, src)
  .cache()

// Shortest Path
// 1 (Toy Story) -> 10 (GoldenEye) -> 9 (Sudden Death)  
// Filter out only the ones with dest as the destination vertex
val shortestPathFromSrcToDest = shortestPathGraph.vertices
  .filter(_._1 == dest)
  .map(_._2)
  .collect()(0)._2

import org.apache.spark.graphx._

import scala.reflect.ClassTag

def drawGraph[VD:ClassTag,ED:ClassTag](g:Graph[VD,ED]) = {
  val u = java.util.UUID.randomUUID
  val v = g.vertices.collect.map(_._1)
  println("""%html
<div id='a""" + u + """' style='width:960px; height:500px'></div>
<style>
.node circle { fill: gray; }
.node text { font: 10px sans-serif;
             text-anchor: middle;
             fill: white; }
line.link { stroke: gray;
            stroke-width: 1.5px; }
</style>
<script src="//d3js.org/d3.v3.min.js"></script>
<script>
var width = 960, height = 500;

var svg = d3.select("#a""" + u + """").append("svg")
    .attr("width", width).attr("height", height);

var nodes = [""" + v.map("{id:" + _ + "}").mkString(",") + """];
var links = [""" + g.edges.collect.map(
  e => "{source:nodes[" + v.indexWhere(_ == e.srcId) + "],target:nodes[" +
       v.indexWhere(_ == e.dstId) + "]}").mkString(",") + """];

var link = svg.selectAll(".link").data(links);
link.enter().insert("line", ".node").attr("class", "link");

var node = svg.selectAll(".node").data(nodes);
var nodeEnter = node.enter().append("g").attr("class", "node")

nodeEnter.append("circle").attr("r", 8);

nodeEnter.append("text").attr("dy", "0.35em")
         .text(function(d) { return d.id; });

d3.layout.force().linkDistance(50).charge(-200).chargeDistance(300)
   .friction(0.95).linkStrength(0.5).size([width, height])
   .on("tick", function() {
      link.attr("x1", function(d) { return d.source.x; })
          .attr("y1", function(d) { return d.source.y; })
          .attr("x2", function(d) { return d.target.x; })
          .attr("y2", function(d) { return d.target.y; });
      node.attr("transform", function(d) {
        return "translate(" + d.x + "," + d.y + ")";
      });
   }).nodes(nodes).links(links).start();
</script>
""")
}

drawGraph(shortestPathGraph)
