import org.apache.spark.sql.SaveMode
// create the actresses_temp table from the input JSON
val actressesDF = sqlContext.read.format("json").load("file:/root/pipeline/datasets/hollywood/actresses.json")
actressesDF.registerTempTable("actresses_temp")
val actressesExplodedDF = sqlContext.sql("SELECT (90000 + people.index) as id, people.name.text as name, people.bio.text as bio, people.hero.src as img FROM actresses_temp LATERAL VIEW EXPLODE (results.people) p AS people")

// create the actors_temp table from the input JSON
val actorsDF = sqlContext.read.format("json").load("file:/root/pipeline/datasets/hollywood/actors.json")
actorsDF.registerTempTable("actors_temp")
val actorsExplodedDF = sqlContext.sql("SELECT (10000 + people.index) as id, people.name.text as name, people.bio.text as bio, people.hero.src as img FROM actors_temp LATERAL VIEW EXPLODE (results.people) p AS people")

// union the 2 tables through the DataFrame API
val actressesAndActorsDF = actressesExplodedDF.unionAll(actorsExplodedDF).cache()

// save as a permanent table (Hive metastore) using the DataFrame API - overwrite whatever was there before
actressesAndActorsDF.write.mode(SaveMode.Overwrite).saveAsTable("actresses_and_actors_perm")
